function Pkn_Dirichlet_arb(k, n, θ)
    θ_arb = RR(θ)
    return θ_arb^k // risingfac(θ_arb,n) * unsigned_Stirling1(n,k)
end

"""
    Pkn_Dirichlet(k, n, θ)

Compute the prior probability mass to obtain k clusters for a Dirichlet process.

# Examples
```julia-repl
julia> Pkn_Dirichlet(7, 10, 1.5)
0.012025126576210167
```
"""
Pkn_Dirichlet(k, n, θ) = convert(Float64, Pkn_Dirichlet_arb(k, n, θ))


function Pkn_Dirichlet_Mult_arb(k, n, H,  θ)
    H_arb = RR(H)
    θ_arb = RR(θ)
    return (fac(H)//(fac(H - k)*risingfac(θ_arb,n))) *((-1)^k )* Cnk(n, k,-θ_arb//H_arb)
end

"""
    Pkn_Dirichlet_Mult(k, n, H, θ)

Compute the prior probability mass to obtain k clusters for a Dirichlet multinomial process.

# Examples
```julia-repl
julia> Pkn_Dirichlet_Mult(7, 10,100, 1.5)
0.0100815973559968
```
"""

Pkn_Dirichlet_Mult(k, n, H, θ) = convert(Float64, Pkn_Dirichlet_Mult_arb(k, n,H,θ))


function Pkn_PDM_arb(k::N, n::N,H::N, θ::T, σ::T) where {T<:Number, N<:Integer}
    H_arb = RR(H)
    θ_arb = RR(θ)
    σ_arb= RR(σ)
    coef= fac(H)// fac(H -k)
    return coef* sum([ (Vnk_2PD(n, i, θ, σ) * unsigned_Stirling2(i,k)* Cnk(n, i, σ))// ((H_arb* σ_arb)^i)  for i in k:n])
  end

  """
      Pkn_PYM(k, n, H, θ)

  Compute the prior probability mass to obtain k clusters for a Pitma-Yor multinomial process.

  # Examples
  ```julia-repl
  julia> Pkn_PYM(7, 10, 100,  1.5, 0.2)
  0.05547962896578268
  ```
  """

Pkn_PYM(k, n, H, θ, σ) = Pkn_PDM_arb(k,n,H,θ, σ) |> Float64


function Pkn_NGGM_arb(k::N, n::N,H::N,  β::T, σ::T) where {T<:Number, N<:Integer}
    H_arb = RR(H)
    return (fac(H) // (H_arb^k * fac(H-k))) * sum([ (1//(H_arb^i)) * unsigned_Stirling2(i+k,k)* Pkn_NGG_arb(k+i, n,  β, σ)  for i in 0:(n-k)])
end


"""
    Pkn_NGGM_direct(k, n, H, β, σ)

Compute the prior probability mass to obtain k clusters for a normalized generalized gamma multinomial process. (direct method that involves computation of prior distribution of the number of clusters for NGG process)
Computationally extensive for multiple k.
# Examples
```julia-repl
julia> Pkn_NGGM_direct(7, 10, 100,  1.0, 0.25)
0.005029270051158085
```
"""
Pkn_NGGM_direct(k, n, H, β, σ) = convert(Float64, Pkn_NGGM_arb(k, n, H, β, σ))

function Pkn_NGGM_arb_precomputed(k::N, n::N,H::N,  β::T, σ::T, Pk_NGG::A) where {T<:Number, N<:Integer,A<:Vector}
    #Pk_NGG = Pkn_NGG_raw.(1:n, n,  β, σ)
    H_arb = RR(H)
    return (fac(H) // (H_arb^k * fac(H-k))) * sum([ (1//(H_arb^i))*unsigned_Stirling2(i+k,k)* Pk_NGG[k+i] for i in 0:(n-k)])
end

"""
    Pkn_NGGM_precomp(k, n, H, β, σ, Pk_NGG)

Compute the prior probability mass to obtain k clusters for a normalized generalized gamma multinomial process, using previously computed prior distribution of the number of clusters for NGG process (Pk_NGG).
# Examples
```julia-repl
julia> Pkn_NGGM(7, 10, 100,  1.0, 0.25, ?)
```
"""

Pkn_NGGM_precomp(k,n, H, β, σ, Pk_NGG) = convert(Float64, Pkn_NGGM_arb_precomputed(k, n, H, β, σ, Pk_NGG))


function Pkn_NGGM_full(n::N, H::N, β::T, σ::T) where {T<:Number, N<:Integer}
    Pk_NGG = Pkn_NGG_arb.(1:n, n,  β, σ)
    H_arb = RR(H)
    #return (fac(H_arb) // (H_arb^k * fac(H_arb-RR(k)))) * sum([ (1//(H_arb^i))*unsigned_Stirling2(i+k,k)* Pk_NGG[k+i] for i in 0:(n-k)])
    return [(fac(H) // (H_arb^k * fac(H-k))) * sum([ (1//(H_arb^i))*unsigned_Stirling2(i+k,k)* Pk_NGG[k+i] for i in 0:(n-k)]) for k in 1:n]
end

"""
    Pkn_NGG_mult(k, n, H, β, σ, Pk_NGG)

Compute the prior distribution for a normalized generalized gamma multinomial process, computing firstly the prior distribution of the number of clusters for NGG process.
# Examples
```julia-repl
julia> Pkn_NGG_mult(10, 100,  1.0, 0.25)
```
"""

Pkn_NGG_mult(n, H, β, σ) = convert(Array{Float64,1}, Pkn_NGGM_full(n, H, β, σ))
